// src/ConfettiButton.tsx
import React, { useState, useCallback } from 'react';
import confetti from 'canvas-confetti';
import { styles } from './popper';

const ConfettiButton: React.FC = () => {
  const [isPopupVisible, setPopupVisible] = useState(false);
  const [hasAnimated, setHasAnimated] = useState(false);

  const handleClick = useCallback(() => {
    if (!hasAnimated) {
      // Trigger confetti animation
      confetti({
        particleCount: 300,
        spread: 90,
        origin: { x: 1, y: 0.9 },
      });

      confetti({
        particleCount: 300,
        spread: 90,
        origin: { x: 0, y: 0.9 },
      });

      setHasAnimated(true);
    }

    setPopupVisible(true);
  }, [hasAnimated]);

  const closePopup = () => {
    setPopupVisible(false);
    setHasAnimated(false); // Reset the animation state when popup closes
  };

  return (
    <div style={styles.container}>
      <button style={styles.fireBtn} onClick={handleClick}>
        Click Me!
      </button>
      {isPopupVisible && (
        <div style={styles.popup}>
          <div style={styles.popupContent}>
            <h2>Popup Card</h2>
            <p>This is a popup card!</p>
            <button onClick={closePopup} style={styles.closeBtn}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};



const Finnal: React.FC = () => {
  return (
    <div>
      <ConfettiButton />
    </div>
  );
};

export default Finnal;
