// src/styles.ts
export const styles: { [key: string]: React.CSSProperties } = {
    container: {
      margin: 0,
      height: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      position: 'relative',
    },
    fireBtn: {
      background: '#90ee90',
      color: '#000',
      fontWeight: 'bold',
      fontSize: '20px',
      padding: '8px 32px',
      border: 'none',
      cursor: 'pointer',
    },
    popup: {
      position: 'absolute',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      backgroundColor: '#fff',
      padding: '20px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
      borderRadius: '8px',
      zIndex: 10,
    },
    popupContent: {
      textAlign: 'center',
    },
    closeBtn: {
      marginTop: '10px',
      backgroundColor: '#f44336',
      color: '#fff',
      border: 'none',
      padding: '8px 16px',
      cursor: 'pointer',
    },
  };
