// src/ConfettiButton.tsx
import React, { useState, useCallback } from 'react';
import confetti from 'canvas-confetti';
import styled from 'styled-components';

// Styled components
const Container = styled.div`
  margin: 0;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
`;

const FireButton = styled.button`
  background: #90ee90;
  color: #000;
  font-weight: bold;
  font-size: 20px;
  padding: 8px 32px;
  border: none;
  cursor: pointer;
`;

const Popup = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: #fff;
  padding: 20px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  border-radius: 8px;
  z-index: 10;
`;

const PopupContent = styled.div`
  text-align: center;
`;

const CloseButton = styled.button`
  margin-top: 10px;
  background-color: #f44336;
  color: #fff;
  border: none;
  padding: 8px 16px;
  cursor: pointer;
`;

// ConfettiButton Component
const ConfettiButton= () => {
  const [isPopupVisible, setPopupVisible] = useState(false);
  const [hasAnimated, setHasAnimated] = useState(false);

  const handleClick = useCallback(() => {
    if (!hasAnimated) {
      // Trigger confetti animation
      confetti({
        particleCount: 300,
        spread: 90,
        origin: { x: 1, y: 0.9 },
      });

      confetti({
        particleCount: 300,
        spread: 90,
        origin: { x: 0, y: 0.9 },
      });

      setHasAnimated(true);
    }

    setPopupVisible(true);
  }, [hasAnimated]);

  const closePopup = () => {
    setPopupVisible(false);
    setHasAnimated(false); 
  };

  return (
    <Container>
      <FireButton onClick={handleClick}>
        Click Me!
      </FireButton>
      {isPopupVisible && (
        <Popup>
          <PopupContent>
            <h2>Popup Card</h2>
            <p>This is a popup card!</p>
            <CloseButton onClick={closePopup}>
              Close
            </CloseButton>
          </PopupContent>
        </Popup>
      )}
    </Container>
  );
};

// const Finnal: React.FC = () => {
